CREATE TABLE trabajador (
        id int not null auto_increment primary key,
        nombre varchar(250) NOT NULL,
        apePaterno varchar(75) NOT NULL,
        apeMaterno varchar(75) NOT NULL,
        rfc varchar(13) NOT NULL,
        curp varchar(18) NOT NULL,
