        sub_unidad varchar(255) NOT NULL,
        categoria varchar(255) NOT NULL,
        horas varchar(255) NOT NULL,
        plaza varchar(255) NOT NULL,
        motivo varchar(255) NOT NULL,
		puesto varchar(255) NOT NULL,
        ingresoGob varchar(20) NOT NULL,
        ingresoSep varchar(20) NOT NULL,
        ingresoDgeti varchar(20) NOT NULL,
        observaciones text NOT NULL,
        ##foreign key (id) references docentes_tablas(id),
        created_at datetime NOT NULL default NOW(),
        updated_at datetime NOT NULL default NOW()
    )engine=InnoDB default charset=utf8mb4;
    
